INSERT INTO dieta VALUES (3333,1661,1161,3,"Mattino: frullati o succhi di frutta (tiepidi o temperatura ambiente); oppure infusi di frutta od erbe o tè, addolciti se si desidera, con miele (evitare lo zucchero) con biscotti o fette biscottate e torte semplici alla frutta, semi integrali od integrali.",8026);
INSERT INTO dieta VALUES (3334,2424,1924,3,"Mezzo giorno: aprire il pasto con un bel piatto di verdure crude di tutti i tipi in abbondanza con frutta in insalata (meglio senza olio, se lo si desidera, utilizzarne pochissime quantità - un cucchiaino al massimo e variando ad ogni pasto il tipo di vegetale dal quale proviene l’olio).",8048);
INSERT INTO dieta VALUES (3335,1458,958,5,"Pomeriggio: frutta di stagione o infusi di erbe (non eccitanti) addolciti con miele, con qualche biscotto, pochissimi; se si desidera cibo salato, fare attenzione alla quantita' ed al tipo.",8115);
INSERT INTO dieta VALUES (3336,2114,1614,3,"Sera: (un piatto solo al massimo) minestre sopra tutto di verdura con, semolini, tapioca, riso, od in mancanza riso o pastina semi integrale; meglio le altre soluzioni. Oppure in sostituzione, pesce e/o carne alla griglia con verdure cotte  o crude,  insalate verdi non crude perche' possono provocare insonnia.",8137);
